INSERT INTO `html_portlet` VALUES (2, '<ul class="features"> \n <li>Lutece is FreeSoftware. Full OpenSource licensed under BSD.</li> \n <li>Full responsive design Back and Front</li> \n <li>Compliant with Bootstrap 3.x themes</li> \n <li>Very modular and flexible architecture based on plugins, APIs, IoC</li> \n <li>Over 300 plugins available for many needs : Content Management, Collaborative, Workflows, ...</li> \n <li>Runs on Java Platform and rely on powerful build tools such as Apache Maven</li> \n <li>Uses best of breed Java Open Source stacks : Lucene, Spring, Ehcache, Freemarker, ...</li> \n</ul> \n<p class="logo">&nbsp;</p>');
INSERT INTO `html_portlet` VALUES (4, '<h2>Try it now ! Login as admin user</h2> \n<p class="mb20">The "<em>admin</em>" user has all rights and features.<br>The password is "<em>adminadmin</em>".</p> \n<p><a class="btn btn-outline-primary" title="Access to admin [Open  in new window]" href="jsp/admin/AdminLogin.jsp" target=""><span class="fa fa-plug">&nbsp;</span> Display Admin Login Form</a></p> \n<h2>Get you own copy</h2> \n<p><img src="images/local/skin/docker-lutece.png" alt="" height="24px"> A docker package is available on the <a title="Docker repository [Open  in new window]" href="https://registry.hub.docker.com/u/lutece/lutece-demo/" target="_blank">Docker repository</a>.</p>');
